import React, { Component } from "react";
import { Table } from "react-bootstrap";

import "./css/Total.css";

export default class Total extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      TotalPrice: 0
    }
  }

  componentDidMount() {
    this.setTotalPrice();
  }

  setTotalPrice = () => {
    this.setState({
      TotalPrice: this.props.TotalPrice
    })
  }

  render() {
    this.setTotalPrice;
    return (
      <div className="Total">
        <Table className="Total-text">
          <tr>
            <th>Total:</th>
            <th>{"£ " + this.state.TotalPrice}</th>
          </tr>
        </Table>
      </div>
    );
  }
}
